import React from 'react';
import { Text, View, Image, StyleSheet, Dimensions } from 'react-native';
const screen = Dimensions.get('window');

const Details = ({ movie, director }) => {
    return (
      <View style={styles.infoCard}>
        <View style={styles.textInfo}>
          <Text style={styles.title}>{movie.original_title}</Text>
          <Text style={{ color: 'green', fontWeight: 'bold' }}>
            {movie.vote_average}
        </Text>
          <Text style={{ color: 'white', fontWeight: 'bold' }}>DESCRIPTION</Text>
          <Text style={{ color: 'white', fontSize: 10 }}>
            {movie.overview.length < 100
              ? movie.overview
              : movie.overview.substr(0, 100) + '...'}
          </Text>
          <View style={{ flexDirection: 'row', alignItems: 'center' }}>
            
          </View>
          <>
            <Text style={{ color: 'white', fontWeight: 'bold' }}>DIRECTEUR</Text>
            <Text style={{ color: 'white', fontSize: 10 }}>{director?.name}</Text>
          </>
        </View>
      </View>
    );
};
export default Details;

const styles = StyleSheet.create({
  infoCard: {
    right: 5,
    top: 10,
    paddingRight: 10,
    borderRadius: 10,
    overflow: 'hidden',
  },
  title: {
    color: 'white',
    fontSize: 25,
    fontWeight: 'bold',
  },
  textInfo: {
    left: 10,
    right: 10,
    flex: 1,
    justifyContent: 'space-evenly',
  },
});
