import React from 'react';
import { Text, View,  StyleSheet, Dimensions } from 'react-native';
const screen = Dimensions.get('window');
import { Ionicons } from '@expo/vector-icons';
const MovieDetails = ({ movie, director }) => {
  return (
    <View style={styles.MovieDetails}>
      <View style={styles.textInfo}>
        <Text style={styles.title}>{movie.original_title}</Text>
        <Text style={{ color: 'black', fontWeight: 'bold', marginVertical:10 ,textDecorationLine: 'underline' }}>Overview :</Text>
        <Text style={{ color: 'black', fontSize: 16, margin:5 }}>
          {movie.overview}
        </Text>
        <Text style={{ color: 'black', fontWeight: 'bold', marginVertical:5 ,textDecorationLine: 'underline'}}>Score :</Text>
        <View style={{ flexDirection: 'row', alignItems: 'center' , marginBottom:10}}>
          <Text style={{ color: 'black', fontWeight: 'bold' }}>
            {movie.vote_average}
          </Text>
          <ion-icon name="star" color="gold"/>
        </View>
        <>
          <Text style={{ color: 'black', fontWeight: 'bold', marginVertical:2 ,textDecorationLine: 'underline' }}>Director :</Text>
          <Text style={{ color: 'black', fontSize: 14  , marginVertical:10 }}>{director?.name}</Text>
        </>
      </View>
    </View>
  );
};

export default MovieDetails;

const styles = StyleSheet.create({
  MovieDetails: {
    bottom: 10,
    left: 3,
    right: 30,
    top: 10,
    paddingRight: 20,
    backgroundColor: '#d6d6d6',
    overflow: 'hidden',
  },
  poster: {
    width: screen.width * 0.3,
  },
  title: {
    color: 'black',
    fontSize: 16,
    fontWeight: 'bold',
  },
  textInfo: {
    left: 10,
    right: 10,
    justifyContent: 'space-evenly',
  },
});
