import React, { useEffect, useState } from 'react';
import {Text,View,StyleSheet,Image,TextInput,Dimensions,FlatList,TouchableOpacity} from 'react-native';
import { Card } from 'react-native-paper';
import { EvilIcons } from '@expo/vector-icons';
import Constants from 'expo-constants';
import axios from 'axios';
import {fetchMovies} from "../services/services.js";

const screen = Dimensions.get('screen');

const HomeScreen = ({ navigation }) => {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchNow, setSearchNow] = useState(false);

  useEffect(() => {
    fetchMovies(searchTerm, movies).then((data) => {
      setMovies(data);
    });
  }, [searchNow]);

  return (
    <View style={styles.container}>
      <View>
        <View style={styles.inputCard}>
          <TextInput
            style={styles.input}
            placeholder={'search movies'}
            value={searchTerm}
            onChangeText={(text) => setSearchTerm(text)}
            onChange={()=>setSearchNow(!searchNow)}
          />
        </View>
        <View style={styles.movieListCard}>
          <FlatList
            data={movies}
            numColumns={2}
            renderItem={({ item, index }) => {
              return (
                <View >
                  <TouchableOpacity
                    onPress={() => {
                      navigation.navigate('Movie', { movie: item });
                    }}>
                    <View style={styles.movieCard}>
                      <Image
                          source={{
                          uri: `http://image.tmdb.org/t/p/w780${item.poster_path}`,
                        }}
                        style={{ width: 100, height: 200 , flex:1 }}
                      />
                    </View>
                    <View style={{ width: Constants.width, height: 200 , flex:3 }}>
                      <Text style={styles.movieTitle}>{item.title}</Text>
                      
                      <Text style={styles.movieOverview}>{item.overview}</Text>
                      <Text style={{ fontWeight:'bold', marginLeft:10 ,marginVertical:20}}>{item.title}</Text>
                      <Text style={styles.movieOverview}>{item.vote_avrage}</Text>
                      <Text style={{fontWeight:'bold', marginLeft:10 ,marginVertical:20}}>{item.release_date}</Text>
                    </View>
                  </TouchableOpacity>
                </View>
              );
            }}
          />
        </View>
      </View>
    </View>
  );
};

export default HomeScreen;

const styles = StyleSheet.create({
  banner: { width: Constants.width, height: 200 },
  bannerInfoCard: {
    position: 'absolute',
    bottom: 0,
    paddingHorizontal: 10,
    paddingTop: 10,
    paddingBottom: 50,
    right: 0,
    left: 0,
    backgroundColor: 'rgba(21,21,21,0.5)',
  },
  bannerTitle: {
    color: 'white',
    fontSize: 16,
    letterSpacing: 1.2,
  },
  bannerOverview: {
    color: 'grey',
  },
  container: {
    flex: 1,
    paddingTop: Constants.statusBarHeight,
    backgroundColor: '#212121',
  },
  inputCard: {
    position: 'absolute',
    margin: 20,
    left: 10,
    right: 10,
    flexDirection: 'row',
    backgroundColor: 'white',
    alignItems: 'center',
    borderRadius: 5,
    zIndex: 100,
  },
  input: {
    padding: 10,
    flex: 1,
  },
  movieCard: {
    flex: 1,
    height: 200,
    margin: 5,
    borderWidth: 5,
  },
  movieListCard: {
    flexDirection: 'row',
    top: screen.height * 0.10,
  },
});
